import { useState } from 'react';
import './App.css';


function App() {

  const [count,setCount]=useState(0);

  const counter = () => {
    setCount(count+1);
    console.log(count);
  }

  const alt = num =>{
    setCount(Number(count)+num);
  }

  const timer=()=>{setInterval(
    alt
  ,1000,2)};

  const stop=()=>{clearInterval(timer)};


  return (
   <>
  <button onclick={timer}>시작</button>
  <button onClick={stop}>멈추기</button>
  <p>{count}</p>
   </>
  );
}

export default App; //App이란 이름으로 다른 js파일에서 사용된다.
