import { useState } from 'react';
import './App.css';


function App() {

  const [count,setCount]=useState(10);
  const alt = num =>{
    if(count>0){
    setCount(count-1);
    }
  }

  const timer=setInterval(
    alt
  ,1000);

  const stop=()=>{clearInterval(timer)};


  return (
   <>
   <p>{count}</p>
  <button onClick={stop}>멈추기</button>
   </>
  );
}

export default App; //App이란 이름으로 다른 js파일에서 사용된다.
