import './App.css';


function App({id='not',children='없음'}) {


  const alt = (name,age) =>{
    alert(name+'은'+ age+'살입니다');
  }

  const timer=setInterval(
    alt
  ,2000,'홍길동','14');

  const stop=()=>{clearInterval(timer)};


  return (
   <>
  <button onClick={stop}>{children}</button>
   </>
  );
}

export default App; //App이란 이름으로 다른 js파일에서 사용된다.
