import { useState } from 'react';
import './App.css';


function App() {

  const [count,setCount]=useState(10);
  var stop=false;
  const alt = num =>{
    if(count>0&&!stop){
    setCount(count-1);
    }
  }

  const start=()=>{setTimeout(
   ()=>{ 
    stop=false;
    alt();
   }
  ,1000)};

  const stopper=()=>{
    stop=true;
  };

  const timer = ()=>{
    setTimeout(alt,1000);
  };

  if(count<10)
  {
    timer();
  }

  return (
   <>
   <p>{count}</p>
   <button onClick={start}>시작</button>
  <button onClick={stopper}>멈추기</button>
   </>
  );
}

export default App; //App이란 이름으로 다른 js파일에서 사용된다.
